script:
frappe.ui.form.on('Sync Settings', {
    refresh(frm) {
        // Add Force Sync button in form header
        frm.add_custom_button('Force Sync', () => {
            let d = new frappe.ui.Dialog({
                title: 'Force Sync Modules',
                fields: [
                    { fieldtype: 'Check', fieldname: 'items', label: 'Items' },
                    { fieldtype: 'Check', fieldname: 'customers', label: 'Customers' },
                    { fieldtype: 'Check', fieldname: 'item_prices', label: 'Item Prices' },
                    { fieldtype: 'Check', fieldname: 'price_lists', label: 'Price Lists' },
                    { fieldtype: 'Check', fieldname: 'sales_invoices', label: 'Sales Invoices' }
                ],
                primary_action_label: 'Sync Now',
                primary_action(values) {
                    let selected = [];
                    for(let key in values){
                        if(values[key]) selected.push(key);
                    }

                    if(selected.length === 0){
                        frappe.msgprint('Please select at least one module to sync');
                        return;
                    }

                    // Call backend API
                    frappe.call({
                        method: 'sync_master.sync_master.api.force_sync',
                        args: { modules: selected },
                        callback: function(r) {
                            if(r.message.success){
                                frappe.show_alert({ message: 'Force sync started for: ' + selected.join(', '), indicator: 'green' });
                            } else {
                                frappe.msgprint('Force sync failed!');
                            }
                            d.hide();
                        }
                    });
                }
            });
            d.show();
        });
    }
});

frappe.ui.form.on('Sync Settings', {
    refresh(frm) {
        // Add single Install Native Cron button
        frm.add_custom_button('Install Native Cron', () => {
            frappe.call({
                method: 'sync_master.sync_master.api.setup_cron',
                callback: function(r) {
                    if (r.message && r.message.success) {
                        frappe.msgprint('Native cron installed successfully!');
                    } else {
                        frappe.msgprint('Failed to install cron. Check logs.');
                    }
                }
            });
        });
    }
});
